    public function getSourceContext()
    {
        return new Source("<!DOCTYPE html>

<title>Calendar index</title>

{% block body %}
    <h1>Calendar index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Title</th>
                <th>Start</th>
                <th>End</th>
                <th>Description</th>
                <th>Background_color</th>
                <th>Text_color</th>
                <th>Border_color</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for calendar in calendars %}
            <tr>
                <td>{{ calendar.id }}</td>
                <td>{{ calendar.title }}</td>
                <td>{{ calendar.start ? calendar.start|date('Y-m-d H:i:s') : '' }}</td>
                <td>{{ calendar.end ? calendar.end|date('Y-m-d H:i:s') : '' }}</td>
                <td>{{ calendar.description }}</td>
                <td>{{ calendar.backgroundColor }}</td>
                <td>{{ calendar.textColor }}</td>
                <td>{{ calendar.borderColor }}</td>
                <td>
                    <a href=\"{{ path('app_calendar_show', {'id': calendar.id}) }}\">show</a>
                    <a href=\"{{ path('app_calendar_edit', {'id': calendar.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"10\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_calendar_new') }}\">Create new</a>
{% endblock %}
", "calendar/index.html.twig", "C:\\xampp\\htdocs\\piDev-DevMinds2024 (1)\\piDev-DevMinds2024\\templates\\calendar\\index.html.twig");
